Script.script:
function cnl_ui.msg(msg,level)
  cecho("<orange>[ UI ] "..msg.."\n")
end

function u.getModPath(mod)
if getModulePath(mod) then return getModulePath(mod):gsub("/[^/]+$","").."/" else return getMudletHomeDir().."/" end
end

function cnl_ui.defaultConfig()
  return {
    enable           = false,           -- cnl_ui is on or off
    layout_mode      = 1,
    layout_show      = false,
    screen           = {
      mainWidthInChars  = 80,             -- width in chars of main window 80,100,120 etc
      --font              = "Jetbrains Mono",
      font              = "Cascadia Mono Light",
      font_size         = 14,
      bgR               = 0,
      bgG               = 0,
      bgB               = 0,
      bgT               = 0,
    },
    cons               = {
      n                 = {
        enable            = false,
        layout_col        = "ansi_cyan",
        dim               = 5,
        dimUnit           = "row",
      },
      e                 = {
        enable            = false,
        layout_col        = "ansi_cyan",        
        dim               = 50,
        dimUnit           = "percent",        
      },
      s                 = {
        enable            = false,
        layout_col        = "ansi_cyan",        
        dim               = 5,
        dimUnit           = "row",        
      },
      w                 = {
        enable            = false,
        layout_col        = "ansi_cyan",        
        dim               = 50,
        dimUnit           = "percent",       
      },
      ne                = {
        enable            = false,
        layout_col        = "ansi_magenta",        
      },
      se                = {
        enable            = false,
        layout_col        = "ansi_magenta",        
      },
      sw                = {
        enable            = false,
        layout_col        = "ansi_magenta",        
      },
      nw                = {
        enable            = false,
        layout_col        = "ansi_magenta",        
      },
    },
  }
end

function cnl_ui.saveConfig()
  u.saveTable(cnl_ui.cfg_path,cnl_ui.cfg)
end

function cnl_ui.setWidthBorder(border,per) per = math.max(math.min(per,100),0)
local altBorder = "w" if border == "w" then altBorder = "e" end
cnl_ui.cfg.cons[border].dim = per cnl_ui.cfg.cons[altBorder].dim = 100 - per cnl_ui.saveConfig() cnl_ui.drawFullUI() cnl_ui.msg("border width "..border.." set to "..per) cnl_ui.msg("border width "..altBorder.." set to "..(100-per)) end

function cnl_ui.setScreen(cfg) setFont(cfg.font) setFontSize(cfg.font_size) setBackgroundColor(0,0,0)
if cfg.n then setBorderTop(cfg.n) end if cfg.e then setBorderRight(cfg.e) end if cfg.s then setBorderBottom(cfg.s) end if cfg.w then setBorderLeft(cfg.w) end
setBackgroundColor( cfg.bgR or 0, cfg.bgG or 0, cfg.bgB or 0, cfg.bgT or 255 ) setWindowWrap(cfg.mainWidthInChars) end
